import React from "react";

class Pokemon extends React.Component {
  render() {
    return (
      <ul className="eachPokemon">
        <li>{this.props.pokemon.name}</li>
        <li>{this.props.pokemon.type}</li>
        <li>{`Average weight: ${this.props.pokemon.averageWeight.value} ${this.props.pokemon.averageWeight.measurementUnit}`}</li>
      </ul>
    )
  }
}

export default Pokemon
